pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "./CharacterNFTManager.sol";
import "./CharacterNFTTokenURI.sol";

contract CharacterNFT is ERC721, AccessControl {
    using Strings for uint256;

    /** VARIABLES **/

    struct CharacterData {
        string className;
        string classImage;
        string classDescription;
    }

    mapping(uint256 => CharacterData) public classIdToCharacterData;
    address public characterNFTManagerContract;
    address public tokenUriContract;

    /** ROLES **/

    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    /** ERRORS **/

    error InvalidValues();

    /** MODIFIERS **/

    /** FUNCTIONS **/

    constructor() ERC721("Diablo", "D2NFT") {
        // DEFAULT_ADMIN_ROLE is a special role that acts as the default admin role for all roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OWNER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);

        classIdToCharacterData[1].className = "BARBARIAN";
        classIdToCharacterData[2].className = "NECROMANCER";
        classIdToCharacterData[3].className = "PALADIN";
        classIdToCharacterData[4].className = "SORCERESS";
        classIdToCharacterData[5].className = "AMAZON";
        classIdToCharacterData[6].className = "ASSASSIN";
        classIdToCharacterData[7].className = "DRUID";
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Set the address of the CharacterNFTManager contract
     */
    function setCharacterNFTManager(
        address characterNFTManagerAddress
    ) public onlyRole(OWNER_ROLE) {
        characterNFTManagerContract = characterNFTManagerAddress;
    }

    /**
     * @dev Set the address of the CharacterNFTTokenURI contract
     */
    function setTokenUriContract(
        address tokenUriContractAddress
    ) public onlyRole(OWNER_ROLE) {
        tokenUriContract = tokenUriContractAddress;
    }

    /**
     * @dev Returns the name of a given token ID
     */
    function tokenName(uint256 tokenId) public view returns (string memory) {
        uint256 classEnumValue = uint256(
            CharacterNFTManager(characterNFTManagerContract)
                .tokenIdToCharacterClass(tokenId)
        );
        string memory className = classIdToCharacterData[classEnumValue]
            .className;
        return string(abi.encodePacked(className, " #", tokenId.toString()));
    }

    /**
     * @dev Returns the URI for a given token ID
     */
    function tokenURI(
        uint256 tokenId
    ) public view override returns (string memory) {
        _requireOwned(tokenId);
        if (tokenUriContract == address(0)) {
            return "";
        }
        return CharacterNFTTokenURI(tokenUriContract).tokenURI(tokenId);
    }

    /**
     * @dev Update the class names
     */
    function updateClassNames(
        uint256 classEnumValue,
        string memory classNames
    ) public onlyRole(OWNER_ROLE) {
        classIdToCharacterData[classEnumValue].className = classNames;
    }

    /**
     * @dev Update the class images
     */
    function updateClassImages(
        uint256 classEnumValue,
        string memory classImages
    ) public onlyRole(OWNER_ROLE) {
        classIdToCharacterData[classEnumValue].classImage = classImages;
    }

    /**
     * @dev Update the class descriptions
     */
    function updateClassDescriptions(
        uint256 classEnumValue,
        string memory classDescriptions
    ) public onlyRole(OWNER_ROLE) {
        classIdToCharacterData[classEnumValue]
            .classDescription = classDescriptions;
    }

    /**
     * @dev Mint a new token, only a minter can call this function
     */
    function mint(uint256 tokenId, address to) public onlyRole(MINTER_ROLE) {
        if (tokenId == 0 || to == address(0)) {
            revert InvalidValues();
        }
        _safeMint(to, tokenId);
    }

    /**
     * @dev Burn a token, only a burner can call this function
     */
    function burn(uint256 tokenId) public onlyRole(BURNER_ROLE) {
        if (tokenId == 0) {
            revert InvalidValues();
        }
        _burn(tokenId);
    }

    function getClassDescription(
        uint256 tokenId
    ) public view returns (string memory) {
        uint256 classEnumValue = uint256(
            CharacterNFTManager(characterNFTManagerContract)
                .tokenIdToCharacterClass(tokenId)
        );
        return classIdToCharacterData[classEnumValue].classDescription;
    }

    function getClassImage(
        uint256 tokenId
    ) public view returns (string memory) {
        uint256 classEnumValue = uint256(
            CharacterNFTManager(characterNFTManagerContract)
                .tokenIdToCharacterClass(tokenId)
        );
        return classIdToCharacterData[classEnumValue].classImage;
    }

    function getClassName(uint256 tokenId) public view returns (string memory) {
        uint256 classEnumValue = uint256(
            CharacterNFTManager(characterNFTManagerContract)
                .tokenIdToCharacterClass(tokenId)
        );
        return classIdToCharacterData[classEnumValue].className;
    }
}
","keccak256":"0xbcbce91362dda57eb5f22ea6986e9153fa70254ac36401c8b425f61c2898118f","license":"MIT"},"contracts/erc721/CharacterNFTManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../BaseHelper.sol";
import "./CharacterNFT.sol";

contract CharacterNFTManager is BaseHelper {
    /** ENUMS **/
    enum CharacterClass {
        NULL,
        BARBARIAN,
        NECROMANCER,
        PALADIN,
        SORCERESS,
        AMAZON,
        ASSASSIN,
        DRUID
    }

    /** VARIABLES **/

    // Track the last token ID minted
    uint256 public tokenIdCounter;
    // Track the character class of each token ID
    mapping(uint256 => CharacterClass) public tokenIdToCharacterClass;
    // Price per mint
    uint256 public pricePerMint;
    // CharacterNFT contract
    address public characterNFT;

    /** ERRORS **/

    error InsufficientFunds(uint256 pricePerMint, uint256 amount);
    error InvalidCharacterClass();

    /** MODIFIERS **/

    /** FUNCTIONS **/

    function initialize(address initialOwner) public initializer {
        _baseInitialize(initialOwner);
    }

    /**
     * @dev Set the price per mint
     */
    function setPricePerMint(
        uint256 _pricePerMint
    ) public onlyRole(OWNER_ROLE) {
        pricePerMint = _pricePerMint;
    }

    /**
     * @dev Set the address of the CharacterNFT contract
     */
    function setCharacterNFT(
        address _characterNFT
    ) public onlyRole(OWNER_ROLE) {
        characterNFT = _characterNFT;
    }

    /**
     * @dev Withdraw the contract balance
     */
    function withdraw() public onlyRole(OWNER_ROLE) {
        payable(msg.sender).transfer(address(this).balance);
    }

    /**
     * @dev Mint a character NFT, open for public use, requires payment
     */
    function mintCharacterPublic(
        uint256 amount,
        CharacterClass charType
    ) public payable whenNotPaused nonReentrant {
        if (charType == CharacterClass.NULL) {
            revert InvalidCharacterClass();
        }
        if (amount == 0) {
            revert InvalidValues();
        }
        if (msg.value < pricePerMint * amount) {
            revert InsufficientFunds(pricePerMint, amount);
        }
        for (uint256 i = 0; i < amount; i++) {
            tokenIdCounter++;
            uint256 newTokenId = tokenIdCounter;
            tokenIdToCharacterClass[newTokenId] = charType;
            CharacterNFT(characterNFT).mint(newTokenId, msg.sender);
        }
    }

    /**
     * @dev Mint a character NFT, only for MINTER use, no payment required
     */
    function mintCharacterPrivate(
        uint256 amount,
        address to,
        CharacterClass charType
    ) public onlyRole(MINTER_ROLE) {
        _mintChar(amount, to, charType);
    }

    /**
     * @dev Mint a batch of character NFTs, only for owner use, no payment required
     */
    function mintBatch(
        uint256[] calldata amounts,
        address[] calldata toAddresses,
        CharacterClass[] calldata charTypes
    ) public onlyRole(MINTER_ROLE) {
        if (
            amounts.length != toAddresses.length ||
            amounts.length != charTypes.length
        ) {
            revert InvalidValues();
        }

        for (uint256 i = 0; i < amounts.length; i++) {
            _mintChar(amounts[i], toAddresses[i], charTypes[i]);
        }
    }

    /**
     * @dev Internal mint helper
     */
    function _mintChar(
        uint256 amount,
        address to,
        CharacterClass charType
    ) internal {
        if (
            charType == CharacterClass.NULL || amount == 0 || to == address(0)
        ) {
            revert InvalidValues();
        }

        for (uint256 i = 0; i < amount; i++) {
            tokenIdCounter++;
            uint256 newTokenId = tokenIdCounter;
            tokenIdToCharacterClass[newTokenId] = charType;
            CharacterNFT(characterNFT).mint(newTokenId, to);
        }
    }
}
","keccak256":"0xc637dd0e44d9f5bc39d0708eb915b1c46758678dfb568e8ac094e2f866166996","license":"MIT"},"contracts/erc721/CharacterNFTTokenURI.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

import "../BaseHelper.sol";
import "./CharacterNFT.sol";
import "../leveling/Level.sol";

contract CharacterNFTTokenURI is BaseHelper {
    using Strings for uint256;
    /** STRUCTS **/

    struct Attribute {
        string name;
        uint256 value;
    }

    /** VARIABLES **/

    address public characterNFT;

    address public levelContractAddress;

    /** ERRORS **/

    /** MODIFIERS **/

    /** FUNCTIONS **/

    function initialize(address initialOwner) public initializer {
        _baseInitialize(initialOwner);
    }

    /**
     * @dev Set the address of the CharacterNFT contract
     */
    function setCharacterNFT(
        address _characterNFT
    ) public onlyRole(OWNER_ROLE) {
        characterNFT = _characterNFT;
    }

    /**
     * @dev Set the address of the Level contract
     */
    function setLevelContract(
        address _levelContract
    ) public onlyRole(OWNER_ROLE) {
        levelContractAddress = _levelContract;
    }

    /**
     * @dev Returns the URI for a given token ID
     */
    function tokenURI(uint256 tokenId) public view returns (string memory) {
        return _createURI(tokenId);
    }

    /**
     * @dev Creates the URI for a given token ID
     */
    function _createURI(uint256 tokenId) internal view returns (string memory) {
        string memory attributesString = "";

        attributesString = string.concat(
            _addAttribute(
                "Character Class",
                CharacterNFT(characterNFT).getClassName(tokenId)
            ),
            ","
        );

        uint256 level = Level(levelContractAddress).getTokenLevel(tokenId);
        uint256 xp = Level(levelContractAddress).getTokenXp(tokenId);

        Attribute[] memory attributes = new Attribute[](2);
        attributes[0] = Attribute("Level", level);
        attributes[1] = Attribute("Xp", xp);

        for (uint256 i = 0; i < attributes.length; i++) {
            if (i == attributes.length - 1) {
                attributesString = string.concat(
                    attributesString,
                    _addAttribute(
                        attributes[i].name,
                        Strings.toString(attributes[i].value)
                    )
                );
            } else {
                attributesString = string.concat(
                    attributesString,
                    _addAttribute(
                        attributes[i].name,
                        Strings.toString(attributes[i].value)
                    ),
                    ","
                );
            }
        }

        string memory propertiesJSON = string.concat(
            '"name":"',
            CharacterNFT(characterNFT).tokenName(tokenId),
            '",',
            '"description":"',
            CharacterNFT(characterNFT).getClassDescription(tokenId),
            '",',
            '"image":"',
            CharacterNFT(characterNFT).getClassImage(tokenId),
            '",',
            '"external_url":"',
            "https://diablo.fandom.com/wiki/Diablo_II",
            '"'
        );
        string memory fullOpenSeaMetadata = string.concat(
            "{",
            propertiesJSON,
            ",",
            '"attributes":[',
            attributesString,
            "]}"
        );
        return
            string.concat(
                "data:application/json;base64,",
                Base64.encode(bytes(fullOpenSeaMetadata))
            );
    }

    function _addAttribute(
        string memory key,
        string memory value
    ) internal pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    '{"trait_type": "',
                    key,
                    '", "value": "',
                    value,
                    '"}'
                )
            );
    }
}
","keccak256":"0x6c7957aec1ce587a77f3ab32c169991f70983e33ddd72f83267a15e20537c74b","license":"MIT"},"contracts/leveling/Level.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "../BaseHelper.sol";

contract Level is BaseHelper {
    /** STRUCTS **/

    // Packed struct
    struct LevelData {
        uint128 level;
        uint128 xp;
    }

    /** VARIABLES **/

    // Track the Level and Xp of each token ID
    mapping(uint256 => LevelData) public tokenIdToLevelData;

    // Each level has an xp requirement
    uint256[] public xpPerLevel;

    // CharacterNFT contract
    address public characterNFT;

    /** ERRORS **/

    error MaxLevelReached();

    /** MODIFIERS **/

    /** EVENTS **/

    /// @notice An event emitted when a character gets xp
    event LevelUp(uint256 indexed tokenId, uint256 level, uint256 xp);

    /** FUNCTIONS **/

    function initialize(address initialOwner) public initializer {
        _baseInitialize(initialOwner);
        // Each level has an xp requirement, start with 10 levels, we can add more later
        xpPerLevel.push(0); // Ignore the 0 index
        xpPerLevel.push(0); // Everyone starts at level 1 with 0 xp so index 1 is 0
        xpPerLevel.push(100); // Level 2 is 100 xp
        xpPerLevel.push(200); // Level 3 is 200 xp
        xpPerLevel.push(300); // Level 4 is 300 xp
        xpPerLevel.push(400); // Level 5 is 400 xp
        xpPerLevel.push(500); // Level 6 is 500 xp
        xpPerLevel.push(600); // Level 7 is 600 xp
        xpPerLevel.push(700); // Level 8 is 700 xp
        xpPerLevel.push(800); // Level 9 is 800 xp
        xpPerLevel.push(1000); // Level 10 is 1000 xp
    }

    /**
     * @dev Update the XpPerLevel array if needed
     */
    function updateXpPerLevelArray(
        uint256[] memory _xpPerLevel
    ) external onlyRole(ADMIN_ROLE) {
        xpPerLevel = _xpPerLevel;
    }

    /**
     * @dev Set the address of the CharacterNFT contract
     */
    function setCharacterNFT(
        address _characterNFT
    ) public onlyRole(OWNER_ROLE) {
        characterNFT = _characterNFT;
    }

    /**
     * @dev Utility-contract only. Grant the user experience and increase their level if valid
     */
    function gainExperience(
        uint256 tokenId,
        uint256 xp
    ) public onlyRole(UTILITY_ROLE) {
        // Make sure the token ID is valid
        require(
            IERC721(characterNFT).ownerOf(tokenId) != address(0),
            "Invalid token ID"
        );
        LevelData storage levelData = tokenIdToLevelData[tokenId];
        // Update the users xp
        levelData.xp += uint128(xp);
        // Make sure user cannot go past max level
        if (levelData.level >= xpPerLevel.length - 1) {
            revert MaxLevelReached();
        }
        // User xp cannot surpass level 10
        if (levelData.xp > xpPerLevel[xpPerLevel.length - 1]) {
            revert MaxLevelReached();
        }
        uint256 currentLevel = levelData.level;
        // Update the level if the new xp falls with the range
        for (uint256 i = currentLevel + 1; i < xpPerLevel.length; i++) {
            if (levelData.xp >= xpPerLevel[i]) {
                levelData.level = uint128(i);
                emit LevelUp(tokenId, i, levelData.xp);
            }
        }
    }

    /**
     * @dev Helper view function returns just the level
     */
    function getTokenLevel(uint256 tokenId) public view returns (uint256) {
        if (tokenIdToLevelData[tokenId].level == 0) {
            return 1;
        } else {
            return tokenIdToLevelData[tokenId].level;
        }
    }

    /**
     * @dev Helper view function returns just the xp
     */
    function getTokenXp(uint256 tokenId) public view returns (uint256) {
        return tokenIdToLevelData[tokenId].xp;
    }
}
